pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

interface IConfig {
    function owner() external view returns (address);

    function getShares() external view returns (address);

    function getVAMM() external view returns (address);

    function getSequencer() external view returns (address);

    function getAuthenticate() external view returns (address);

    function acceptedToken() external view returns (IERC20Metadata);
}
","keccak256":"0xb73f1aeb2c4031cc2ca5d5e9e7059270e8b3d8d06366f325b5914016a417f73a","license":"UNLICENSED"},"contracts/interfaces/ILauncher.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.12;

interface ILauncher {
    event AcceptedShare2Earn(
        address from,
        address to,
        uint256 amount,
        uint256 reward
    );

    event BuyMFC(address to, uint256 amount, uint256 price);
    event EarnMFC(address to, uint256 amount);

    struct UserEarn {
        uint256 tgId;
        address user;
        uint256 earnTotal;
    }

    function launchDone() external view returns (bool);

    function getShare2MultiplyAmount(
        address shareId
    ) external view returns (uint256);

    function subShare2MultiplyAmount(
        address from,
        address to,
        uint256 amount,
        uint256 rewardAmoumt
    ) external;

    function getRemainSharePoolAmount() external view returns (uint256);

    function getTribeShare2MultiplyRemainTimes(
        address shareId
    ) external view returns (uint256);

    function computeShare2EarnReward(
        address from,
        address to,
        uint256 amount
    ) external view returns (uint256);

    function getMaxHoldeAmount() external view returns (uint256);

    function isAuthorized(address shareId) external view returns (bool);

    function isBlackList(address account) external view returns (bool);
}
